import React, { useState } from 'react';

const AirQualityForm = () => {
    const [formData, setFormData] = useState({
        pm25: '',
        pm10: '',
        o3: '',
        no2: '',
        so2: '',
        co: ''
    });
    const [result, setResult] = useState(null);

    const handleChange = (e) => {
        setFormData({ ...formData, [e.target.name]: e.target.value });
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        try {
            const response = await fetch('http://127.0.0.1:5000/api/airquality', {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json'
                },
                body: JSON.stringify(formData)
            });
            const data = await response.json();
            setResult(data.aqi);
        } catch (error) {
            console.error('Error:', error);
        }
    };

    return (
        <div>
            <form onSubmit={handleSubmit}>
                <input type="number" name="pm25" onChange={handleChange} placeholder="PM2.5" />
                <input type="number" name="pm10" onChange={handleChange} placeholder="PM10" />
                <input type="number" name="o3" onChange={handleChange} placeholder="O3" />
                <input type="number" name="no2" onChange={handleChange} placeholder="NO2" />
                <input type="number" name="so2" onChange={handleChange} placeholder="SO2" />
                <input type="number" name="co" onChange={handleChange} placeholder="CO" />
                <button type="submit">Tahmin Yap</button>
            </form>
            {result && <h3>Tahmin Edilen AQI: {result}</h3>}
        </div>
    );
};

export default AirQualityForm;
